/**
 * Implements hook_user_role_presave().
 *
 * Inform other modules that a user role is about to be saved.
 *
 * Modules implementing this hook can act on the user role object before
 * it has been saved to the database.
 *
 * @param $role
 *   A user role object.
 *
 * @see hook_user_role_insert()
 * @see hook_user_role_update()
 *
 * $COMMENT$
 */
function $MODULE_NAME$_user_role_presave($role) {
  // Set a UUID for the user role if it doesn't already exist
  if (empty($role->uuid)) {
    $role->uuid = uuid_uuid();
  }
  $END$
}

/**
 * Implements hook_user_role_presave().
 *
 * $COMMENT$
 */
function $MODULE_NAME$_user_role_presave($role) {
  // Set a UUID for the user role if it doesn't already exist
  if (empty($role->uuid)) {
    $role->uuid = uuid_uuid();
  }
  $END$
}

/**
 * Implements hook_user_role_insert().
 *
 * $COMMENT$
 */
function $MODULE_NAME$_user_role_insert($role) {
  // Save extra fields provided by the module to user roles.
  db_insert('my_module_table')
    ->fields(array(
      'rid' => $role->rid,
      'role_description' => $role->description,
    ))
    ->execute();
  $END$
}

/**
 * Implements hook_user_role_update().
 *
 * $COMMENT$
 */
function $MODULE_NAME$_user_role_update($role) {
  // Save extra fields provided by the module to user roles.
  db_merge('my_module_table')
    ->key(array('rid' => $role->rid))
    ->fields(array(
      'role_description' => $role->description
    ))
    ->execute();
  $END$
}

/**
 * Implements hook_user_role_delete().
 *
 * $COMMENT$
 */
function $MODULE_NAME$_user_role_delete($role) {
  // Delete existing instances of the deleted role.
  db_delete('my_module_table')
    ->condition('rid', $role->rid)
    ->execute();
  $END$
}

