/** * Implements hook_field_instance_settings_form(). * * Add settings to an instance field settings form. * * Invoked from field_ui_field_edit_form() to allow the module defining the * field to add settings for a field instance. * * @param $field * The field structure being configured. * @param $instance * The instance structure being configured. * * @return * The form definition for the field instance settings. * * $COMMENT$ */ function $MODULE_NAME$_field_instance_settings_form($field, $instance) { $settings = $instance['settings'];
$form['text_processing'] = array( '#type' => 'radios', '#title' => t('Text processing'), '#default_value' => $settings['text_processing'], '#options' => array( t('Plain text'), t('Filtered text (user selects text format)'), ), ); if ($field['type'] == 'text_with_summary') { $form['display_summary'] = array( '#type' => 'select', '#title' => t('Display summary'), '#options' => array( t('No'), t('Yes'), ), '#description' => t('Display the summary to allow the user to input a summary value. Hide the summary to automatically fill it with a trimmed portion from the main post.'), '#default_value' => !empty($settings['display_summary']) ? $settings['display_summary'] : 0, ); }
return $form; $END$ }

/**
 * Implements hook_field_instance_settings_form().
 *
 * $COMMENT$
 */
function $MODULE_NAME$_field_instance_settings_form($field, $instance) {
  $settings = $instance['settings'];

  $form['text_processing'] = array(
    '#type' => 'radios',
    '#title' => t('Text processing'),
    '#default_value' => $settings['text_processing'],
    '#options' => array(
      t('Plain text'),
      t('Filtered text (user selects text format)'),
    ),
  );
  if ($field['type'] == 'text_with_summary') {
    $form['display_summary'] = array(
      '#type' => 'select',
      '#title' => t('Display summary'),
      '#options' => array(
        t('No'),
        t('Yes'),
      ),
      '#description' => t('Display the summary to allow the user to input a summary value. Hide the summary to automatically fill it with a trimmed portion from the main post.'),
      '#default_value' => !empty($settings['display_summary']) ? $settings['display_summary'] :  0,
    );
  }

  return $form;
  $END$
}

/**
 * Implements hook_field_widget_settings_form().
 *
 * Add settings to a widget settings form.
 *
 * Invoked from field_ui_field_edit_form() to allow the module defining the
 * widget to add settings for a widget instance.
