/**
 * Implements hook_aggregator_parse().
 *
 * $COMMENT$
 */
function $MODULE_NAME$_aggregator_parse($feed) {
  if ($items = mymodule_parse($feed->source_string)) {
    $feed->items = $items;
    return TRUE;
  }
  return FALSE;
  $END$
}

